let burgerBtn=document.querySelector("#burger-btn"),mainmenu=document.querySelector("#main-menu"),canSeeMenu=!1;burgerBtn.addEventListener("click",(()=>{console.log(canSeeMenu),canSeeMenu?(mainmenu.classList.add("show-menu"),canSeeMenu=!1):(mainmenu.classList.add("show-menu"),canSeeMenu=!0)}));let closeBtn=document.querySelector("#close-btn");closeBtn.addEventListener("click",(()=>{mainmenu.classList.remove("show-menu"),canSeeMenu=!1}));